package info.nukoneko.android.gameoflife;import android.graphics.Canvas;import android.view.MotionEvent;import java.util.LinkedList;import info.nukoneko.android.gameoflife.controller.CellController;/** * Created by TEJNEK on 2016/03/21. */final class GameManager {    private LinkedList<BaseController> mTaskList = new LinkedList<>();    GameManager(MainSurface surface){        mTaskList.add(new CellController(surface));    }    boolean onUpdate(){        for(BaseController t : this.mTaskList){            if(!t.onUpdate()) this.mTaskList.remove(t);        }        return true;    }    boolean onTouchEvent(MotionEvent event){        for(BaseController t : this.mTaskList){            t.onTouchEvent(event);        }        return true;    }    void onDraw(Canvas c){        c.drawARGB(255, 0,0,0);        for(BaseController t : this.mTaskList){            t.onDraw(c);        }    }}