package info.nukoneko.android.gameoflife;import android.graphics.Canvas;import android.view.MotionEvent;import java.util.LinkedList;import info.nukoneko.android.gameoflife.controller.CellController;/** * Created by TEJNEK on 2016/03/21. */final public class GameManager {    final MainSurface parent;    private LinkedList<BaseController> mTaskList = new LinkedList<>();    public GameManager(MainSurface surface){        this.parent = surface;        mTaskList.add(new CellController(parent));    }    public boolean onUpdate(){        for(BaseController t : this.mTaskList){            if(!t.onUpdate()) this.mTaskList.remove(t);        }        return true;    }    public boolean onTouchEvent(MotionEvent event){        for(BaseController t : this.mTaskList){            t.onTouchEvent(event);        }        return true;    }    public void onDraw(Canvas c){        c.drawARGB(255, 0,0,0);        for(BaseController t : this.mTaskList){            t.onDraw(c);        }    }}