package info.nukoneko.android.gameoflife;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.Window;import android.view.WindowManager;/** * Created by TEJNEK on 2016/03/21. */final public class MainActivity extends AppCompatActivity {    MainSurface surface;    @Override    protected void onCreate(Bundle savedInstanceState) {        getWindow().addFlags(WindowManager.LayoutParams.FLAG_KEEP_SCREEN_ON);        supportRequestWindowFeature(Window.FEATURE_NO_TITLE);        super.onCreate(savedInstanceState);        getWindow().addFlags(WindowManager.LayoutParams.FLAG_FULLSCREEN);        this.surface = new MainSurface(this);        setContentView(this.surface);    }    @Override    public void onWindowFocusChanged(boolean hasFocus) {        super.onWindowFocusChanged(hasFocus);    }}